INSERT INTO `user_detail` VALUES (1548889371405492225, 3, '罗老师', 0, '/img-tx/1.jpg', '', '', NULL, '讲师', NULL, NULL, NULL, 'Sklearn机器学习工具源码贡献者；Google Tensorflow中文社区开发者；具有多年企业级AI项目开发经验，对推荐系统，NLP，知识图谱，问答系统有较深入研究。', '/img-tx/b473f39c6501431f9e29c7a0beb4c668.jpg', 3, NULL, '2022-07-18 12:36:18', '2022-11-08 15:49:11', 1, 1, 0);
