INSERT INTO `user_detail` VALUES (1548940777449611265, 3, '王老师', 0, '/img-tx/header_default.png', '', '', NULL, '讲师', NULL, NULL, NULL, '拥有9年设计经验，曾服务北京正邦品牌策划，百度用户体验中心，北京资海科技集团，龙采科技集团，优逸客科技教育集团，洛杉矶张大钦律师事务所等众多一线互联网设计与外包平台。', '/img-tx/262241a6efd147c9b3c849561720429f.jpg', 3, NULL, '2022-07-18 16:00:34', '2022-11-08 15:49:25', 1, 1, 0);
INSERT INTO `user_detail` VALUES (1548940921662365698, 3, '李老师', 0, '/img-tx/header_default.png', '', '', NULL, '讲师', NULL, NULL, NULL, 'Apache Flink源码贡献者。专注于亿级用户规模的大数据平台研发建设和研发，历任项目经理、工程师、技术总监等职务', '/img-tx/8d025db3e8ed4e47a4bbdd3fe485f03e.jpg', 3, NULL, '2022-07-18 16:01:09', '2022-11-08 15:49:31', 1, 1, 0);
INSERT INTO `user_detail` VALUES (1548941239125041153, 3, '郎老师', 0, '/img-tx/header_default.png', '', '', NULL, '讲师', NULL, NULL, NULL, '先后在国内IT名企担任软件开发工程师、项目经理等职务。 掌握Java SE，Java Web、MySQL、Redis以及Dubbo、ZooKeeper 、SpringBoot、SpringCloud等分布式架构技术以及Vue.js等前端框架', '/img-tx/62e4c4c5a6de428cb327691554f44787.jpg', 3, NULL, '2022-07-18 16:02:25', '2022-11-08 15:49:37', 1, 1, 0);
INSERT INTO `user_detail` VALUES (1548941336596471809, 3, '孙老师', 0, '/img-tx/header_default.png', '', '', NULL, '讲师', NULL, NULL, NULL, '人工智能博士，曾就职多家知名互联网人工智能公司，善于并引入深度学习领域的新进展。在算法架构及算法模型方面都有较深的造诣，在多模态、内容理解及内容安全、情感计算等方面具备多年实践经验，发表国内外论文多篇。项目经历：多模态内容标签、多模态安全检测、知识图谱', '/img-tx/fc3052bdb2e84106a7a2809fa660b333.jpg', 3, NULL, '2022-07-18 16:02:48', '2022-11-08 15:49:45', 1, 1548941336596471809, 0);
INSERT INTO `user_detail` VALUES (1549358501685448706, 3, '李老师', 0, '/img-tx/header_default.png', '', '', NULL, '讲师', NULL, NULL, NULL, '10多年IT从业经验，北大计算机毕业，曾在中科软从事系统分析与架构工作，精通OOM、PD、DDD建模；对计算机原理、体系结构、常用算法有深入研究；熟练分布式和微服务环境下的主流技术架构。', '/img-tx/65b7682b82c24946b58b77730588a4d2.jpg', 3, NULL, '2022-07-19 19:40:27', '2022-11-08 15:49:52', 1, 1, 0);
